import React from 'react';
import axios from "axios";
import { useRef } from "react";
import { useState, useEffect } from "react";
export const SERVER_URL = process.env.REACT_APP_SERVER_URL;

function Registrarse() {

    const nombre = useRef();
    const nombreusuario = useRef();
    const mail = useRef();
    const contrasena= useRef();
    const contrasena2 = useRef();
  const origen = useRef();

    const [current_id, setCurrentId] = useState(100);

    useEffect(() => {
        const data = window.localStorage.getItem("current_id");
        if  ( data !==null) setCurrentId(JSON.parse(data));
    }, []);

    useEffect(() => {
        window.localStorage.setItem("current_id", JSON.stringify(current_id));
    }, [current_id]);

    const registrarse = async () => {


        const url = `${SERVER_URL}/registro`;
        const body = {
            mail: mail.current.value,
            password1: contrasena.current.value,
            password2: contrasena2.current.value,
            nombre: nombre.current.value,
            username: nombreusuario.current.value,
            origen: origen.current.value,
          };
        await axios
          .post(url, body)
          .then((response) => {
            setCurrentId(response.data);
            alert(`Registro correcto!`);
            window.location.href = "/simulador"
          })
          .catch((error) =>
            alert(`[${error.response.status}] ${error.response.data}`)
          );
    };

    return (
        <>
        <div className="row">
        <div className = "column" id="registro_columna">
                    <img id="registro_foto" src={require("../imgs/registro.png")} alt="Imagen Registrarse"/>
        </div>

        <div className="column" id="contenedor_registro">
            <h1 id="registro">Regístrate Aquí</h1>
            <br></br>

            <form className="registro" id="registro">
                <label className="label_registro" id="registro">Nombre: </label>
                <input type="text" id="nombre" className="input" placeholder="Carlos Huerta" ref={nombre} required/><br/>
                <label className="label_registro">Username: </label>
                <input type="text" id="nombre_usuario" className="input" placeholder="CHuerta123" ref={nombreusuario} required/><br/>
                <label className="label_registro">Email:   </label>
                <input type="text" id="mail" className="input" placeholder="chuerta@uc.cl" ref={mail} required /><br/>

              <label className="label_registro">Origen:</label>
              <select id="origen" className='input' ref={origen} required>
                    <option value="0" disabled selected>Seleccione una opción</option>
                    <option value="Chile" selected>Chile</option>
                    <option value="Perú">Perú</option>
                    <option value="Argentina">Argentina</option>
                </select>

              <br></br>
              {/* <input type="text" id="origen" className="input" placeholder="Chile" ref={origen} required /><br></br> */}
                <label className="label_registro">Contraseña: </label>
                <input type="password" id="contrasena" className="input" ref={contrasena} required/><br></br>
                <label className="label_registro">Repita su Contraseña: </label>
                <input type="password" id="contrasena2" className="input" ref={contrasena2} required/><br></br>
            </form>
            <br></br>

            <button className="btn btn-primary btn-lg" id="boton_registro" onClick={registrarse}>Registrarse</button> 

        </div>

        <div className="column">

        </div>

    </div>   
        </>
    )
}


export default Registrarse;
