import Tile from './Tile';
import Tabla from './Tabla';
import React, {useRef, useEffect }  from "react";
import { useState }  from "react";
import axios from "axios";
import Popup from './Popup';
import { renderMatches } from 'react-router-dom';
export const SERVER_URL = process.env.REACT_APP_SERVER_URL;

function Simulador(){

    const secar = useRef();
    const tostar = useRef();
    const triturar = useRef();
    const prensar = useRef();
    const cantidad = useRef();
    const tile = useRef();
    // let current_id = 0;
    const [current_id, setCurrentId] = useState(100);

    // Recupero Cual es el current_id
    useEffect(() => {
        const data = window.localStorage.getItem("current_id");
        if  ( data !==null) setCurrentId(JSON.parse(data));
    }, []);

    //-------//
    // POST, guardar simulador en base de datos y obtener producto creado
    //-------//
    const simulador = async () => {
        const url = `${SERVER_URL}/simulador`;
        const tiles = document.getElementsByClassName("Tile");


        const body = {
            secar: secar.current.value,
            tostar: tostar.current.value,
            triturar: triturar.current.value,
            prensar: prensar.current.value,
            cantidad: cantidad.current.value,
            id: current_id,
        };
        await axios
        .post(url, body)
        .then((response) => {
            // alert(` correcto!`);
            tiles[0].innerHTML= response.data["product"]
            return 0

        })
        .catch((error) =>
            alert(`[${error.response.status}] ${error.response.data}`)
        );
    };

    //-------//
    // GET
    //-------//

    const [data, setData] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            setIsLoading(true);
            const url = `${SERVER_URL}/simulador/${current_id}`;
            const data = await axios.get(url);
            setData(data.data);
            setIsLoading(false);
        };
        fetchData();
    }, [setData]);

    function recargar() {
        const fetchData = async () => {
            setIsLoading(true);
            const url = `${SERVER_URL}/simulador/${current_id}`;
            const data = await axios.get(url);
            setData(data.data);
            setIsLoading(false);
        };
        fetchData();

    }

    // Para el tutorial
    const [isOpen, setIsOpen] = useState(false);
    const [page, setPage] = useState(0);

    function togglePopup ()  {
        setIsOpen(!isOpen);
    }

    function nextPage ()  {
        setPage(page+1);
    }

    function prevPage ()  {
        if (page > 0) setPage(page-1);
    }

    function showcontent(page){
        if (page==0){
            return (
                <div className='popup'>
                <h2>Cómo usar el simulador</h2>
                <br></br>
                <p> A continuación se muestra el paso a paso para usar el simulador. Si ya sabes usarlo puedes cerrar el tutorial. Si no presiona siguiente!</p>
            </div>
            )
        }
        if (page==1){
            return (
                <div className='popup'>
                <h2>Cómo usar el simulador</h2>
                <br></br>
                <p> 1. Ingresar la cantidad de cacao que se desea procesar. Cada unidad representa un fruto de cacao adicional. </p>
                <img src={require("../imgs/foto1.png")} alt="Imagen"> </img>

            </div>

            )
        }
        if (page==2){
            return (
                <div className='popup'>
                <h2>Cómo usar el simulador</h2>
                <br></br>
                <p> 2. Seleccionar los procesos a realizar apretando los botones. </p>
                <p> 3. Presionar el botón "Simular" para ver el resultado. </p>

            </div>
            )
        }
