import React from 'react';
import chocolate from "../imgs/chocolate-bar.png";
import cacao from "../imgs/cocoa.png";
import { useState }  from "react";


function Tile(props) {

    const urls = [chocolate, cacao];

    const [id, setId] = useState(0);

    function change_id(symbol){
      if (symbol == "chocolate"){
        setId(0);
      } else {
        setId(1);
      }
      return id;
    }

    return (
      <>
      <section className="Tile">
        <p id="p_tile">{props.symbol}</p>
      </section>
      <section className="Tile">
        <p>  {change_id(props.symbol)}</p>
        <img src={urls[change_id(props.symbol)]} alt="img" className='img_tile' width={100}></img>
        </section></>

    );
  }

  export default Tile;
